import React from "react";


function GetData() {

  const [allData, setData] = React.useState(null);
  let stop = false;

  React.useEffect(() => {

    void async function fetchData() {

      while(!stop){
        try {
          await fetch("http://localhost:3001/api", {
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }
          })
            .then((res) => res.json())
            .then((data) => setData(data.docs));

            if(allData === null){ stop = true; }

        } catch (error) {
          console.log('error', error);
        }
      }
    }();
  }, [stop]);

  console.log(allData);
  return allData;

}

export default GetData;
