import React from "react";
import RenderProjects from "./dataSet";
import RenderTypes from "./ProjectTypes";

class Project extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: null,
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch("http://localhost:3001/api", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    isLoaded: true,
                    items: result
                });
            });
    }

    render() {

        const search = window.location.search;
        const params = new URLSearchParams(search);
        const tag = params.get('tag');
        const type = params.get("type");


        const { items } = this.state;

        const loadingStyle = { "padding": "10%" }
        const filteredData = items.docs.filter((a) => a.tag === tag);

        if ( tag === "python" && type === null) {

            return <RenderTypes items={filteredData} />
        } else {
            if (!this.state.isLoaded) {
                return <div style={loadingStyle}> Loading.... </div>
            } else {
                return <RenderProjects items={filteredData} />
            }
        }
    }
}

export default Project;
