import react from "react";
import "./TopBar.css";
// import logo from "./logo.png";
import avatar from "./avatar.png";
// import { CSSAnimation, CSSTransition } from "react-transition-group";
import sound from "./aud1.wav";
import useSound from 'use-sound';
import { Button } from "antd";

function TopBar() {

    const [play, { stop }] = useSound( sound, { volume: 0.5 } );

    const [isHovering, setIsHovering] = react.useState(
        false
    );


    return (


        <section id="top-section">

            <nav className="uk-navbar-container uk-navbar-transparent" uk-navbar='true'>

                <div className="uk-navbar-left">

                    <ul className="uk-navbar-nav">
                        <li className="uk-active uk-logo"><a href="."> <img src={avatar} alt='' /> </a> </li>
                    </ul>

                </div>

                <div className="uk-navbar-right">

                    <ul className="uk-navbar-nav">
                        <li className="uk-active">   <a href="/">   <button className="uk-button uk-button-text"> Works  </button>  </a> </li>
                        <li className="uk-active">   <a href="/">   <button className="uk-button uk-button-text"> contact  </button>  </a> </li>
                    </ul>

                </div>

            </nav>



            <Button
                onMouseEnter={() => {
                    setIsHovering(true);
                    play();
                }}
                onMouseLeave={() => {
                    setIsHovering(false);
                    stop();
                }}
            >
                <ButtonContents isHovering={isHovering}>
                    Hover over me!
                </ButtonContents>
            </Button>



            <div className="info">
                <div className="uk-card info-section uk-card-body">
                    <img id="avatar" src={avatar} alt="" />
                    <h1 id="name">
                        <span className="name" > P </span>
                        <span className="name" > u </span>
                        <span className="name" > r </span>
                        <span className="name" > u </span>
                        <span className="name" > s </span>
                        <span className="name" > h </span>
                        <span className="name" > o </span>
                        <span className="name" > t </span>
                        <span className="name" > h </span>
                        <span className="name" > a </span>
                        <span className="name" > m </span>
                        <span className="name" > a </span>
                        <span className="name n" > n </span>
                        <span className="name" > G </span>
                    </h1>
                    <p style={{ 'transition': 'all 1s' }}> can create beautiful things using
                        <span id="skill-text"> {this.state.skill} </span>
                    </p>
                </div>
            </div>



        </section>
    )
};


export default TopBar;
